const getChartDate = (chartType, apiData) => {
  console.log("Api data", apiData.slice(-10));
  let sliced = apiData.slice(-40);

  return {
    labels: sliced.map(it => it.reportDateString),
    datasets: [
      {
        label: "Total Confirmed Cases",
        fill: false,
        lineTension: 0.1,
        backgroundColor: "rgba(75,192,192,0.4)",
        borderColor: "rgba(75,192,192,1)",
        borderCapStyle: "butt",
        borderDash: [],
        borderDashOffset: 0.0,
        borderJoinStyle: "miter",
        pointBorderColor: "rgba(75,192,192,1)",
        pointBackgroundColor: "#fff",
        pointBorderWidth: 1,
        pointHoverRadius: 5,
        pointHoverBackgroundColor: "rgba(75,192,192,1)",
        pointHoverBorderColor: "rgba(220,220,220,1)",
        pointHoverBorderWidth: 2,
        pointRadius: 1,
        pointHitRadius: 10,
        data: sliced.map(it => it.totalConfirmed)
      },
      {
        label: "China",
        fill: false,
        lineTension: 0.1,
        backgroundColor: "orange",
        borderColor: "orange",
        borderCapStyle: "butt",
        borderDash: [],
        borderDashOffset: 0.0,
        borderJoinStyle: "miter",
        pointBorderColor: "rgba(75,192,192,1)",
        pointBackgroundColor: "#fff",
        pointBorderWidth: 1,
        pointHoverRadius: 5,
        pointHoverBackgroundColor: "rgba(75,192,192,1)",
        pointHoverBorderColor: "rgba(220,220,220,1)",
        pointHoverBorderWidth: 2,
        pointRadius: 1,
        pointHitRadius: 10,
        data: sliced.map(it => it.mainlandChina)
      }
    ]
  };
};

const getChartOptions = type => {
  return [
    {
      type: "time",
      distribution: "series",
      offset: true,
      ticks: {
        major: {
          enabled: true,
          fontStyle: "bold"
        },
        source: "data",
        autoSkip: true,
        autoSkipPadding: 75,
        maxRotation: 0,
        sampleSize: 100
      },
      time: {
        tooltipFormat: "MM/DD/YYYY",
        parser: "YYYY-MM-DD",
        displayFormats: {
          minute: "YYYY-MM-DD",
          hour: "YYYY-MM-DD"
        }
      }
    }
  ];
};

export { getChartDate, getChartOptions };
