import React, { useState } from "react";
import useFetch from "../utils/useFetch";

const CountrySelector = ({ onChange }) => {
  console.log("country selector loading");
  const { stats: countries, error, loading } = useFetch(
    "https://covid19.mathdro.id/api/countries"
  );
  const [selected, setSelected] = useState("USA");

  if (loading) return <p>Countries Loading...</p>;

  if (error) return <p>Error loading countries..</p>;

  return (
    <>
      <select
        onChange={e => {
          onChange(e.target.value);
        }}
      >
        <option key='world' value='world'>
          World
        </option>
        {Object.entries(countries.countries).map(([name, code]) => (
          <option key={name} value={countries.iso3[code]}>
            {name}
          </option>
        ))}
      </select>
    </>
  );
};

export default CountrySelector;
