import React, { useState, useEffect } from "react";
import { Bar } from "react-chartjs-2";
import useFetch from "../utils/useFetch";

const data = {
  labels: ["January", "February", "March", "April", "May", "June", "July"],
  datasets: [
    {
      label: "My First dataset",
      backgroundColor: "rgba(255,99,132,0.2)",
      borderColor: "rgba(255,99,132,1)",
      borderWidth: 1,
      hoverBackgroundColor: "rgba(255,99,132,0.4)",
      hoverBorderColor: "rgba(255,99,132,1)",
      data: [65, 59, 80, 81, 56, 55, 40]
    }
  ]
};

const BarChart = ({ selectedState }) => {
  console.log("Bar Chart", selectedState);

  if (!selectedState) return <></>;

  const [locations, setLocations] = useState({});

  const getData = async () => {
    let response = await fetch(
      "https://coronavirus-tracker-api.herokuapp.com/confirmed"
    );
    let data = await response.json();
    console.log("useEffect bar chart", data.locations);
    setLocations(data.locations);
  };

  useEffect(() => {
    getData();
  }, []);

  let chartData = {}; //getChartDate("line", stats);
  let chartOptions = {}; //getChartOptions("line");

  useEffect(() => {
    chartData = getChartData("bar", stats);
    chartOptions = getChartOptions("bar");
  }, [selectedState]);

  return (
    <>
      <Bar data={chartData} options={chartOptions} />
    </>
  );
};

export default BarChart;
