import React from "react";
import useFetch from "../utils/useFetch";
import styled from "styled-components";
import { Card } from "semantic-ui-react";
import { Grid, Segment } from "semantic-ui-react";

const Stats = React.memo(({ url }) => {
  const { stats, loading, error } = useFetch(url);
  console.log("Stats component loading", stats, loading, error);

  if (loading) return <p>Loading...</p>;

  if (error) return <p>{error.toString()}</p>;

  if (stats.error)
    return <h3>No data found. API error => {stats.error.message}</h3>;

  const items = [
    {
      header: "Coronavirus Cases",
      description: `${stats.confirmed.value}`,
      color: "grey",
      meta: ""
    },
    {
      header: "Deaths:",
      color: "red",
      description: `${stats.deaths.value}`,
      meta: ""
    },
    {
      header: "Recovered",
      color: "green",
      description: `${stats.recovered.value}`,
      meta: ""
    }
  ];

  return (
    <>
      <Grid columns={1} divided>
        <Grid.Row stretched>
          <Grid.Column>
            <Segment>
              <Card.Group itemsPerRow={3} items={items} />
            </Segment>
          </Grid.Column>
          <Grid.Column>
            <Segment>Charts</Segment>
          </Grid.Column>
        </Grid.Row>
      </Grid>
    </>
  );
});

export default Stats;
