import React, { useState } from "react";
import useFetch from "../utils/useFetch";
import { Card, Dropdown } from "semantic-ui-react";

const StateStats = ({ country }) => {
  const [selectedState, setSelectedState] = useState({
    provinceState: undefined
  });

  let cardItems = [];
  if (country === "world") {
    return <p>Select country to see state wise data</p>;
  }

  const { stats: states, loading, error } = useFetch(
    `https://covid19.mathdro.id/api/countries/${country}/confirmed`
  );

  if (loading) return <p>Loading States of selected country...</p>;

  if (error) return <p>Retrieving states API Error => {error.toString()}</p>;

  if (states.error)
    return (
      <h3>
        No data found. Retrieving states API error => {stats.error.message}
      </h3>
    );

  console.log("State wise info", states);

  //const [cardItems, setCardItems] = useState([]);

  let stateOptions =
    states.length === 1
      ? [
          {
            key: states[0].countryRegion,
            value: states[0].countryRegion,
            text: states[0].countryRegion
          }
        ]
      : states.map(({ provinceState }) => ({
          key: provinceState,
          value: provinceState,
          text: provinceState
        }));

  const onChange = selectedValue => {
    let selected = states.find(state => state.provinceState === selectedValue);
    console.log("Selected state info", selected);
    if (!selected) {
      selected = states[0];
    }
    console.log("Selected state info", selected);
    setSelectedState(selected);
  };

  if (selectedState) {
    cardItems = [
      ({
        header: "Confirmed",
        description: `${selectedState.confirmed}`,
        color: "grey",
        meta: ""
      },
      {
        header: "Active:",
        color: "blue",
        description: `${selectedState.deaths}`,
        meta: ""
      },
      {
        header: "Recovered",
        color: "green",
        description: `${selectedState.recovered}`,
        meta: ""
      },
      {
        header: "Deaths",
        color: "red",
        description: `${selectedState.deaths}`,
        meta: ""
      })
    ];
  } else {
    cardItems = [];
  }

  return (
    <>
      <Dropdown
        placeholder='Select State'
        fluid
        search
        selection
        options={stateOptions}
        defaultValue='world'
        onChange={(e, data) => {
          onChange(data.value);
        }}
      />
      <br />
      <Card.Group itemsPerRow={4} items={cardItems} />
    </>
  );
};

export default StateStats;
