import React from "react";
import useFetch from "../utils/useFetch";
import styled from "styled-components";
import { Card } from "semantic-ui-react";

const StatGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  grid-gap: 1rem;
`;
const StatBlock = styled.div`
  background: #f2f2f2;
  font-size: 2rem;
  padding: 2rem;
  border-radius: 2rem;
  display: grid;
  align-items: center;
  justify-items: center;
  text-align: center;
`;

const Stats = React.memo(({ url }) => {
  const { stats, loading, error } = useFetch(url);
  console.log("Stats component loading", stats, loading, error);

  if (loading) return <p>Loading...</p>;

  if (error) return <p>{error.toString()}</p>;

  if (stats.error)
    return <h3>No data found. API error => {stats.error.message}</h3>;

  const items = [
    {
      header: "Coronavirus Cases",
      description: `${stats.confirmed.value}`,
      color: "grey",
      meta: ""
    },
    {
      header: "Deaths:",
      color: "red",
      description: `${stats.deaths.value}`,
      meta: ""
    },
    {
      header: "Recovered",
      color: "green",
      description: `${stats.recovered.value}`,
      meta: ""
    }
  ];

  return (
    <>
      <Card.Group itemsPerRow={3} items={items} />
      {/* <StatGrid>
        <StatBlock>
          <h3>Confirmed:</h3>
          <span>{stats.confirmed.value}</span>
        </StatBlock>
        <StatBlock>
          <h3>Deaths:</h3>
          <span>{stats.deaths.value}</span>
        </StatBlock>
        <StatBlock>
          <h3>Recovered:</h3>
          <span>{stats.recovered.value}</span>
        </StatBlock>
      </StatGrid> */}
    </>
  );
});

export default Stats;
